import ExpenseItem from './ExpenseItem'
import './Expenses.css'
import Card from "../UI/Card"
import ExpensesFilter from './ExpenseFilter'
import { useState } from 'react'



function Expenses(props) {

    const [filterSelectedYear, setfilterSelectedYear] = useState("2020");


   const filterchangeHandler  =( (selectedYear) => {
            setfilterSelectedYear(selectedYear)
   })

    return (
        <div>


        <Card className="expenses">
        <ExpensesFilter selected = {filterSelectedYear} onchangeFilter={filterchangeHandler}></ExpensesFilter>

        return (   { props.items.map((i) => {  <ExpenseItem title={props.expense[i].title} amount={props.expense[i].amount} date={props.expense[i].date}></ExpenseItem>})}


        </Card>

        </div>

    )
}

export default Expenses
