import ExpenseItem from './ExpenseItem'
import './Expenses.css'
import Card from "../UI/Card"
import ExpensesFilter from './ExpenseFilter'
import { useState } from 'react'



function Expenses(props) {

    const [filterSelectedYear, setfilterSelectedYear] = useState("");


   const filterchangeHandler  =( (selectedYear) => {
            setfilterSelectedYear(selectedYear)
   })

   const filteredExpense = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === filterSelectedYear;
   })

   let expenseContent =  <label style={{color : 'white'}}>No   Expenses   Found</label>

    return (
        <div>


        <Card className="expenses">
        <ExpensesFilter selected = {filterSelectedYear} onchangeFilter={filterchangeHandler}></ExpensesFilter>
          {filteredExpense.length > 0 ? filteredExpense.map((expense) => (<ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}></ExpenseItem>) ) : expenseContent}
        </Card>

        </div>

    )
}

export default Expenses
