import ExpenseItem from './ExpenseItem'
import './Expenses.css'
import Card from "../UI/Card"
import ExpensesFilter from './ExpenseFilter'
import { useState } from 'react'



function Expenses(props) {

    const [filterSelectedYear, setfilterSelectedYear] = useState("2020");


   const filterchangeHandler  =( (selectedYear) => {
            setfilterSelectedYear(selectedYear)
   })

    return (
        <div>


        <Card className="expenses">
        <ExpensesFilter selected = {filterSelectedYear} onchangeFilter={filterchangeHandler}></ExpensesFilter>
            <ExpenseItem title={props.expense[0].title} amount={props.expense[0].amount} date={props.expense[0].date}></ExpenseItem>
            <ExpenseItem title={props.items[1].title} amount={props.items[1].amount} date={props.items[1].date}></ExpenseItem>
            <ExpenseItem title={props.items[2].title} amount={props.items[2].amount} date={props.items[2].date}></ExpenseItem>
            <ExpenseItem title={props.items[3].title} amount={props.items[3].amount} date={props.items[3].date}></ExpenseItem>


        </Card>

        </div>

    )
}

export default Expenses
