import { useState } from 'react'
import './ExpenseForm.css'





const ExpenseForm = (props) => {




   const [userInput, setUserInput]  = useState({
        enteredtitle : '',
        enteredAmount : '',
        enteredDate : ''
    })

    function titleChangeHandler(event){


        setUserInput((prevState) => {
            return {...prevState, enteredtitle :event.target.value}
        })


    }
    function amountChangeHandler(event){
        setUserInput((prevState) => {
            return {...prevState, enteredAmount  :event.target.value}
        })

    }

    function dateChangeHandler(event){
        setUserInput((prevState) => {
            return {...prevState, enteredDate  :event.target.value}
        })


    }


    props.expense = userInput;

    return  (
        <form >
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <lable >Title </lable>
                    <input onChange={titleChangeHandler} type="text" min={0.01} step={0.01}/>
                </div>
                <div className='new-expense__control'>
                    <lable >Amount </lable>
                    <input onChange={amountChangeHandler} type="number" min={0.01} step={0.01}/>
                </div>
                <div className='new-expense__control'>
                    <lable>Date </lable>
                    <input  onChange={dateChangeHandler} type="date" min="2019-01-01"  max="2022-01-01" />
                </div>
                <div className='new-expense__actions'>
                    <button type='submit'>Add Expense</button>
                </div>
            </div>

        </form>
    )
}

export default ExpenseForm;
